const express = require("express");
const router = express.Router();

let films = [
  {
    id: 0,
    name: "Film",
    image: "data:image/jpeg;base64,",
    genre: "GG",
    note: "FF",
    watched: true,
  },
  {
    id: 1,
    name: "Film",
    image: "data:image/jpeg;base64,",
    genre: "GG",
    note: "FF",
    watched: false,
  },
  {
    id: 2,
    name: "Film",
    image: "data:image/jpeg;base64,",
    genre: "GG",
    note: "FF",
    watched: false,
  },
  {
    id: 3,
    name: "Film",
    image: "data:image/jpeg;base64,",
    genre: "GG",
    note: "FF",
    watched: false,
  },
];

router.get("/", (req, res) => {
  res.json(films);
});

router.get("/:id", (req, res) => {
  const id = req.params.id;
  let filmId;
  for (let film of films) {
    if (film.id == id) {
      filmId = film;
      break;
    }
  }
  res.json(filmId);
});

router.post("/registration", (req, res) => {
  const film = req.body;
  if (!film) {
    res.send("Erro ao cadastrar o filme!!");
  } else {
    film.id = films[films.length - 1].id + 1;
    films.push(film);
    res.json({ msg: "Cadastrado com sucesso!!" });
  }
});

router.put("/update/:id", (req, res) => {
  const id = req.params.id;
  const { name, image, genre, note, watched } = req.body;
  for (let i = 0; i < films.length; i++) {
    if (films[i].id == id) {
      (films[i].name = name),
        (films[i].image = image),
        (films[i].genre = genre),
        (films[i].note = note),
        (films[i].watched = watched);
    }
  }
  res.json({msg: "Atualizado com sucesso!"})
});

router.delete("/delete/:id", (req, res) => {
  const id = req.params.id;
  for (let i = 0; i < films.length; i++) {
    if (films[i].id == id) {
      films.pop(i);
    }
  }
  res.json({msg: "Deletado com sucesso!"})
});

module.exports = router;
